function _getData() {
  	//x data  -- legend data
    let xdata = [],legendDt=[]; // name = x;type = z;value = y;  
    this.dataSet.forEach((item, i) => {
      if (!xdata.includes(item.x)) {
        xdata.push(item.x);
      }
      if(!legendDt.includes(item.z)){
      	legendDt.push(item.z)
      }
    })
 
    //优化数据顺序
    let obj ={};
	  this.dataSet.forEach((item, i) => {
			!obj[item.z] ? obj[item.z] = [item] : obj[item.z].push(item);
		})	

  	//优化数据顺序
    let dataObj = {},dataLg = [];
    for(let k=0;k<legendDt.length;k++){
    		let kinds = legendDt[k],tempArr = [];
    		
    		for(let b = 0;b<obj[kinds].length;b++){
    				let ind = obj[kinds][b].x && xdata.indexOf((obj[kinds][b].x));
    				tempArr[ind] = obj[kinds][b].y;
    		} 
    		dataObj[kinds] = tempArr;
    		dataLg.push(tempArr.length);
    }

    return { xdata,legendDt, dataObj,dataLg }
  }

function  _getMaxData() {
  	const {dataObj,legendDt,xdata} = _getData();
		let sumArr = [];
		xdata.map((item,index) => {
				let sum = 0;
				legendDt.map((v,i)=>{
						let val = dataObj[v][index] && dataObj[v][index]>0 ?dataObj[v][index]:0;
						sum += Number(val);
				})
				sumArr.push(sum);
    })
//		console.log("xxxxx",xdata);
	const maxNum = Math.max.call(null, ...sumArr);
	const minNum = Math.min.call(null, ...sumArr);
    const maxData = Array(xdata.length).fill(maxNum);
    
    let lev = (parseInt(maxData).toString()).split("")[0],
				lg = parseInt(maxData).toString().length,
				x = parseInt(lev)+ 0.5;
		let _maxFinal = x*Math.pow(10, lg);  
//  console.log(maxNum,_maxFinal);
    return { maxData,_maxFinal,minNum}
  }

function  _getSeries() {
  	const {dataObj,legendDt} = _getData();
    let itemColor = _bgColors();
    let lg = itemColor.length;
    return legendDt.map((item, index) => {

      return {
        stack: "总量",
        type: "bar",
        barWidth: "30%",
        xAxisIndex: 0,
        yAxisIndex: 0,
        name: item,
        data: dataObj[item],
        itemStyle: {
          normal: {
            color:itemColor[index%lg] ,
            barBorderWidth: 0,
            barBorderRadius: 0
          }
        }
      };
    });
  }
function  _iconlist(){
  	return {
	      type: "pictorialBar", 
		  symbol:"image://data:image/png;base64,",
	      symbolSize: [15, 15],
	      symbolRotate: 180,
	      symbolOffset: [0, 20],
	      symbolBoundingData: 300,
	      data: [1,2,3,4,5,6,7,8,9],
	      zlevel: 100,
	      z: 100
	  };
  }
  
function  _bgColors() {
		return [
			new this.echarts.graphic.LinearGradient(0.4, 0.3,0, 1, 
			[
				{offset: 0,color: '#8abaf6'  }, // 1
				{offset: 1, color: '#579af8' } 
			], false),
			new this.echarts.graphic.LinearGradient(0.4, 0.3,0, 1, 
			[
				{offset: 0,color: '#a7a0f0'  }, //2
				{offset: 1, color: '#7e66dd' } 
			], false),
			new this.echarts.graphic.LinearGradient(0.4, 0.3,0, 1, 
			[
				{offset: 0,color: '#92eae0'  }, //3
				{offset: 1, color: '#548a97' } 
			], false),
			new this.echarts.graphic.LinearGradient(0.4, 0.3,0, 1, 
			[
				{offset: 0,color: '#f0d332'  }, //4
				{offset: 1, color: '#f3da71' } 
			], false),
			new this.echarts.graphic.LinearGradient(0.4, 0.3,0, 1, 
			[
				{offset: 0,color: '#d1843a'  }, //5
				{offset: 1, color: '#d09257' } 
			], false)
			
			
		]
  }	



